package Scripts.Entities;import com.google.appengine.api.datastore.Key;import com.google.appengine.api.datastore.KeyFactory;import Frame.Entities.*;import java.util.List;import Users.Entities.*;import javax.jdo.Query;import Users.Entities.*;import Cash.Entities.*;import java.util.Date;public class ScriptsStore extends EntityStoreBase {    public ScriptsStore() {        pm = null;    }		public void Script1(){		BankAccountStore store = new BankAccountStore();		List<BankAccount> results = store.GetAccountDetails();		Date newdate = new Date();		for (BankAccount temp : results) {			temp.setCreatedDate(newdate);			store.UpdateAccount(temp);		}	}		public void Script2(String name,boolean flag){		UserStore store = new UserStore();				List<User> results = store.GetAllUsers();				if(name != ""){			for (User temp : results) {				if(temp.getUserName().toUpperCase() == name.toUpperCase()){					temp.setIsAdmin(flag);					store.UpdateUser(temp);				}			}		}		else{			for (User temp : results) {							temp.setIsAdmin(false);				store.UpdateUser(temp);			}		}	}    }